/*
* npm install vue-router@next
* 配置路由：在 main.js 文件中,可以配置路由以使用 Login.vue 和 Register.vue
*
* index.html 文件已经包含了 <div id="app"></div>,所以不需要添加任何内容。这个 div 元素就是 Vue 应用程序的挂载点。
* 当在 main.js 文件中调用 app.mount("#app") 时,Vue 会查找具有 ID app 的元素,并将应用程序的根组件渲染到该元素中。
* */

// 引入 Vue 模块和 Vue Router 模块
import {createApp} from "vue";
import {createRouter, createWebHistory} from "vue-router";

// 引入 Login 和 Register 组件
import Login from "./Login.vue";
import Register from "./Register.vue";

// 定义路由
const routes = [
    {path: "/login", component: Login},
    {path: "/register", component: Register},
    {path: "/", redirect: "/login"} // 默认重定向到登录页面
];

// 创建路由实例
const router = createRouter({
    history: createWebHistory(),
    routes
});

// 创建 Vue 应用实例
const app = createApp({});

// 使用路由
app.use(router);

// 关闭 Vue 应用在控制台中给出的生产提示
app.config.productionTip = false;

// 将 Vue 实例挂载到具有指定选择器的元素上
app.mount("#app");
